    // tap-highlight-color: transparent;
    // font-smoothing: antialiased;
    font-family: $fontStack;
    font-weight: 300;
    max-width: 100vw;
    // overflow-x: hidden;
}

h1,
h2,
h3,
h4,
h5,
h6,
p,
label,
input,
button,
a,
span,
div {
    font-weight: $fontWeightThin !important;

    &::selection {
        background: $clern-blue;
        color: white;
    }
}


.header {
    position: fixed;
    z-index: 1024;
    width: 100%;
    display: flex;
    top: 16px;
    transition: 0.5s;

    .container-fluid {
        height: 72px;
        position: relative;
        background: transparent;
        border-radius: .0010px;
        transition: $durationM;

        &.scrolled {
            box-shadow: 0 20px 35px rgba(0, 0, 0, 0.2);
            margin-top: 0.25rem;
            border-radius: 200px;
            background: white;
            padding: 0 0.75rem;


            .header {
                color: white;

                &__logo-img {
                    max-height: 32px;
                    margin-left: 1.5rem;
                }
            }
        }
    }

    &__logo {
        display: flex;
        position: relative;
        z-index: 16;
        transition: 0.5s $bezierFastoutSlowin;
        cursor: pointer;
        color: $clern-darkGrey;
        line-height: 2.5rem;
        font-size: $fontSizeBodyL;

        &::after {
            content: '.blog';
            font-style: italic;
            color: $clern-lightGreen;
            position: absolute;
            right: -40px;
            bottom: -7px;
        }

        &:hover {
            transform: scale(1.1);
        }

        &:active {
            transform: scale(1);
        }

        &-img {
            max-height: 48px;
        }

        svg {
            width: 2.5rem;
            height: 2.5rem;
            padding: 0.25rem 0;
            margin-right: 1rem;

            path,
            rect {
                fill: white;
            }
        }
    }

    .nav-toggle {
        // position: fixed;
        top: 1rem;
        right: 2.5rem;
        margin-right: 1rem;
        z-index: 1024;
        display: none;
        background: none;
        border: 0;
        outline: 0;
        height: 48px;
        align-items: center;

        svg {
            color: $clern-darkGrey;
            transition: $bezierFastoutSlowin 0.5s;
            top: 50%;
            left: calc(50% + 1.7px);
            position: absolute;
            width: 28px;
            height: 28px;

            path {
                fill: white;
                transition: $bezierFastoutSlowin 0.5s;
            }
        }

        &__inner {
            position: relative;
        }

        &__icon--menu {
            opacity: 1;
            transform: translate(-50%, -50%);
        }

        &__icon--close {
            opacity: 0;
            transform: translate(-50%, -50%) rotate(45deg);
        }
    }


    .button-icon-only {
        align-items: center;
        justify-content: center;
        padding: 0;
    }


    // &__nav {
    //     flex-grow: 10;
    //     display: flex;
    //     align-items: center;
    //     margin-left: calc(-207px / 2);
    // }

    &__nav-list {
        display: flex;
    }

    &__nav-link {
        color: black;
        align-self: center;
        font-weight: $fontWeightMedium;
        font-size: $fontSizeBodyL;
        text-decoration: none;
        position: relative;
        transition: color $durationS ease .1s;
        line-height: 1;
        padding: 0 $spaceS;
        margin: 0 $spaceS;
        border-radius: 0;
        letter-spacing: $spaceLetters;
        transition: $bezierFastoutSlowin 0.36s;
        font-weight: bold;

        &:active,
        &:hover {
            text-decoration: none;
            color: $clern-blue;

            &:before {
                width: 100%;
            }
        }

        &.active {
            color: white;
        }
    }

    &__nav-left {
        position: relative;

        li {
            list-style-type: none;
            padding: 0;
            margin: 0;
        }

        .magic-line {
            position: absolute;
            bottom: -1rem;
            left: 0;
            padding: 0;
            width: 100px;
            height: 4px;
            border-radius: 200px;
            background: $clern-blue-gradient;
            transition: $durationM;
            opacity: 0;
        }
    }

    &__social-link {
        background: lighten($color: $clern-blue, $amount: 20%);
        width: 42px;
        height: 42px;
        border-radius: 50%;
        display: flex;
        justify-content: center;
        align-items: center;
        position: relative;
        transition: $durationM;

        &::before {
            content: '';
            position: absolute;
            top: 50%;
            left: 50%;
            width: 100%;
            height: 100%;
            background: $clern-blue;
            background: $clern-blue-gradient;
            transition: $durationM;
            border-radius: 50%;
            transform: translate(-50%, -50%) scale(0);
        }

        svg {
            fill: white;
            border-radius: 50%;
            width: 30px;
            height: 20px;
            position: relative;
            z-index: 1;
        }

        &:hover {
            &::before {
                transform: translate(-50%, -50%) scale(1);
            }
        }

        &:active {
            transform: scale(0.9);
        }
    }

    &__search-btn {
        background: white;
        box-shadow: 0px 4px 14px 0px rgba(141, 206, 87, 0.54);
        outline: none;
        border: none;

        &::before {
            background: $clern-lightGreen;
            background: $clern-green-gradient;
            transform: translate(-50%, -50%) scale(1.01);
        }

        &:hover {
            &::before {
                transform: translate(-50%, -50%) scale(0);
            }

            svg {
                path {
                    fill: $clern-lightGreen;
                }
            }
        }

        &:hover,
        &:active,
        &:focus {
            outline: none;
            border: none;
        }

        svg {
            height: 17px;
            transition: $durationM;
        }
    }
}

.main {
    margin-top: 120px;
}

.news-slider {
    width: 100%;


    &__item {
        position: relative;
        min-height: 630px;
        height: 63vh;
    }

    &__img {
        position: absolute;
        max-width: 100%;
        width: 100%;
        height: 100%;
        object-fit: cover;
        margin: auto;
        z-index: -1;
        opacity: 0.1;
    }

    &__img-container::after {
        display: block;
        position: absolute;
        top: 0;
        left: 0;
        background: $clern-blue;
        z-index: -3;
        height: 100%;
        width: 100%;
        content: '';
    }

    &__content {
        margin: 0 auto;
        color: white;
        padding-top: 80px;
    }

    &__category {
        font-size: $fontSizeBodyL;
        border-top-left-radius: 1rem;
        border-bottom-right-radius: 1rem;
        background: white;
        text-align: center;
        padding: 0.5rem;
        box-shadow: $shadow-xl;
        color: white;
        display: block;
        transition: $durationM;
        overflow: hidden;
        position: relative;
        transform: rotateX(1deg);

        span {
            position: relative;
            z-index: 1;
            transition: $durationM;
        }

        &::before {
            content: '';
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            position: absolute;
            background: $clern-green-gradient;
            transition: $durationL;
            border-top-left-radius: 1rem;
            border-bottom-right-radius: 1rem;
            z-index: 1;
        }

        &:hover {
            span {
                color: $clern-blue;
            }

            &::before {
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
                width: 0;
                height: 0;
            }
        }

        &:active {
            transform: scale(0.9);
        }

        &:hover,
        &:focus,
        &:active {
            color: white;
            text-decoration: none;
        }
    }

    &__heading {
        font-size: $fontSizeH1;
    }

    &__deco-line {
        width: 100%;
        height: 3px;
        background-color: white;
        display: inline-block;
        margin-bottom: 1.75rem;
        margin-top: 1.35rem;
    }

    &__read-more {
        -webkit-box-shadow: rgba(20, 184, 235, 0.5) 0px 5px 12px 0px;
        box-shadow: rgba(20, 184, 235, 0.5) 0px 5px 12px 0px;
        z-index: 2;
        background: white;
        border: 2px solid transparent;
    }

    &__read-more a {
        color: $clern-blue;
        text-decoration: none;
    }

    &__read-more:hover {
        background: transparent;
        border-color: white;
    }

    &__read-more:hover a {
        color: white;
    }

    .fa-chevron-left,
    .fa-chevron-right {
        padding: 0.75rem;
        position: absolute;
        top: 40%;
        color: white;
        margin: 0 50px;
        max-width: 70px;
    }

    .fa-chevron-left {
        left: 5%;
    }

    .fa-chevron-right {
        right: 5%;
    }


}



.footer {
    z-index: -1;


    .container-fluid {
        background-color: $clern-grey;
        border-top-left-radius: 15vh;
        width: 100%;
    }



    &--main-site .container-fluid {
        padding-top: 3rem;
        margin-top: -75px;
        padding-top: 150px;
        padding-bottom: 90px;
    }

    .row:last-child {
        border-top: 1px solid rgba(25, 25, 25, 0.1);
    }

    &__logo {
        max-height: 60px;
    }

    &__grey {
        font-size: $fontSizeBodyS;
        opacity: 0.7;
    }

    &__link {
        color: $clern-darkGrey;
        font-size: $fontSizeBodyM;
        transition: $durationS;

        &:hover {
            color: $clern-blue;
            text-decoration: none;
        }
    }

    &__tel,
    &__email {
        color: $clern-blue;
        display: inline-block;
        font-size: $fontSizeH3;
        position: relative;
        overflow: hidden;
    }

    &__tel:hover,
    &__tel:visited,
    &__email:hover,
    &__email:visited {
        text-decoration: none;
        color: $clern-blue;
    }

    &__tel::after,
    &__email::after {
        content: "";
        position: absolute;
        width: 100%;
        height: 2px;
        background-color: $clern-blue;
        bottom: 0;
        right: -100%;
        transition: $durationM;
    }

    &__tel:hover::after,
    &__email:hover::after {
        right: 0;
    }

    &__social-link {
        display: inline-block;
        transition: $durationM;
        border-radius: 50%;
        background: $clern-blue;
        padding: 0.5rem;


        &:hover {
            transform: scale(1.2);
            background: $clern-lightGreen;
        }

        &:active {
            transform: scale(0.9);
        }

        &:focus {
            outline: none;
        }

        svg {
            transform: scale(0.65);
        }

        svg path {
            fill: white;
        }
    }

    &__pay-img {
        opacity: 0.14;
        transition: $durationM;
    }

    &__pay-img:hover {
        opacity: 0.8;
    }
}




.modal-search {
    .form-group {
        margin-bottom: 0;
        display: flex;
        justify-content: space-between;
        align-items: center;
    }

    .form-label {
        font-size: $fontSizeH2;
    }

    .focused .form-label {
        font-size: $fontSizeBodyL;
    }

    .form-input {
        padding-top: 10px;
        max-width: calc(100% - 64px);
        font-size: $fontSizeH2;
        box-shadow: 0 2px 0 0 $clern-darkGrey;
    }

    &__btn {
        width: 56px;
        height: 56px;
        display: inline-flex;
    }

}

@media (max-width:1200px) {

    html,
    body {
        max-width: 100vw;
        overflow-x: hidden !important;
    }

    .header {
        bottom: initial;
        top: 0;
        left: 1rem;
        width: calc(100% - 2rem);
        padding: 0;
        height: 48px;
        max-width: 100vw;
        left: 0;
        left: 1rem;
        right: 1rem;
        width: calc(100vw - 2rem);

        .container-fluid {
            margin-top: 0.75rem;
            padding: 0;
            min-height: initial;

            &.scrolled {
                background: white;

                .header__lang-all {
                    top: initial;
                    width: auto;

                    .container {
                        margin: 0;
                    }
                }
            }
        }


        &__logo {
            padding: 0;

            &-img {
                max-height: 32px;
            }
        }

        &__nav {
            position: fixed;
            top: 0;
            right: 0;
            bottom: 0;
            left: 0;
            background: rgba(255, 255, 255, 0.8);
            transform: translate3d(0, -100%, 0);
            transition-duration: $durationL;
            transition-timing-function: $bezierFastoutSlowin;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            backdrop-filter: blur($spaceS);
            max-width: 100%;
            width: 100%;
            opacity: 0;
            margin: 0;


            &--entered {
                transform: translateZ(0);
                opacity: 1;
            }

            &-right {
                text-align: center;
                width: 100vw;
                max-width: 250px;
            }

        }

        &__nav-list {
            transform: none;
            display: block;
            flex-direction: initial;
        }

        .magic-line {
            display: none;
        }

        &__nav-link {
            width: 100%;
            display: block;
            font-size: $fontSizeBodyL;
            padding: 16px;
            text-align: center;
            color: black !important;
            margin: 0;
        }

        .nav-toggle {
            display: flex;
            background: $clern-lightGreen;
            margin-right: 0;
            width: 48px;
            height: 48px;
            border-radius: 50%;

            &__inner {
                width: 32px;
                height: 32px;
            }

            &--entered {
                .nav-toggle__icon--menu {
                    opacity: 0;
                    transform: translate(-10%, -50%);
                }

                .nav-toggle__icon--close {
                    opacity: 1;
                    transform: translate(-50%, -50%) rotate(0);
                }
            }
        }

        &__lang-active {
            position: relative;
            z-index: 10;
        }

        &__lang-active-text {
            color: $clern-lightGreen;
        }

        &__lang-arrow {
            display: none;
        }

        &__lang-container {
            bottom: 1.5rem;
            left: 50%;
            transform: translateX(-50%);
            color: black;
            background: transparent;
            box-shadow: none;
            width: auto;
            margin-top: 1.5rem;
            display: flex;
            width: 100%;
            justify-content: space-around;
            font-size: 1rem;


            &:hover,
            &:focus,
            &:active {
                background: transparent;
                box-shadow: none;
                border: none;
            }
        }

        &__lang-all {
            display: flex;
            position: initial;
            background: none;
            padding: 0.25rem 1rem;
            width: auto;
            color: black;
            transform: none;
            opacity: 1;
            z-index: 1;
            font-size: 1rem;

            .container {
                margin: 0;
                padding: 0.5rem 1rem;
            }
        }

        &__lang-active {
            width: 72px;
            height: 72px;
            border: 1px solid green;
            border-radius: 50%;
            margin-right: 0.5rem;
            display: -webkit-box;
            align-items: center;
            justify-content: center;
            display: none;
        }

        &__btn {
            margin-top: 0.5rem;
            color: $clern-blue;
            border-color: $clern-blue;
            background: transparent;
            height: 50px;
            line-height: 50px;
        }
    }

}
